<?phpfunction update_disbursement_status($PK_STUDENT_MASTER,$PK_STUDENT_ENROLLMENT){	global $db;		$en_cond = "";	if($PK_STUDENT_ENROLLMENT != '')		$en_cond = " AND PK_STUDENT_ENROLLMENT = '$PK_STUDENT_ENROLLMENT' ";		//if Deposited date and Approved date are null then display nothing	$db->Execute("UPDATE S_STUDENT_DISBURSEMENT SET PK_DISBURSEMENT_STATUS = '0' WHERE APPROVED_DATE = '0000-00-00' AND DEPOSITED_DATE = '0000-00-00' AND PK_STUDENT_MASTER = '$PK_STUDENT_MASTER' AND PK_ACCOUNT = '$_SESSION[PK_ACCOUNT]' ");		//If Deposited date is null and Approved date is set then display "Approved"	$db->Execute("UPDATE S_STUDENT_DISBURSEMENT SET PK_DISBURSEMENT_STATUS = '2' WHERE APPROVED_DATE != '0000-00-00' AND DEPOSITED_DATE = '0000-00-00' AND PK_STUDENT_MASTER = '$PK_STUDENT_MASTER' $en_cond AND PK_PAYMENT_BATCH_DETAIL = 0 AND PK_ACCOUNT = '$_SESSION[PK_ACCOUNT]'");		/*if Deposited date is null and Approved date is set and Disbursement is in a batch then display:    "Hold" (if status is set to "Hold" in the batch)    "In Batch"*/	$res = $db->Execute("SELECT PK_STUDENT_DISBURSEMENT, PK_PAYMENT_BATCH_DETAIL FROM S_STUDENT_DISBURSEMENT WHERE APPROVED_DATE != '0000-00-00' AND DEPOSITED_DATE = '0000-00-00' AND PK_STUDENT_MASTER = '$PK_STUDENT_MASTER' $en_cond AND PK_PAYMENT_BATCH_DETAIL > 0 AND PK_ACCOUNT = '$_SESSION[PK_ACCOUNT]'");	while (!$res->EOF){		$PK_STUDENT_DISBURSEMENT = $res->fields['PK_STUDENT_DISBURSEMENT'];		$PK_PAYMENT_BATCH_DETAIL = $res->fields['PK_PAYMENT_BATCH_DETAIL'];				$res_bat = $db->Execute("SELECT PK_BATCH_STATUS FROM S_PAYMENT_BATCH_MASTER, S_PAYMENT_BATCH_DETAIL WHERE S_PAYMENT_BATCH_MASTER.PK_PAYMENT_BATCH_MASTER = S_PAYMENT_BATCH_DETAIL.PK_PAYMENT_BATCH_MASTER AND PK_PAYMENT_BATCH_DETAIL = '$PK_PAYMENT_BATCH_DETAIL' ");		if($res_bat->fields['PK_BATCH_STATUS'] == 1)			$db->Execute("UPDATE S_STUDENT_DISBURSEMENT SET PK_DISBURSEMENT_STATUS = '3' WHERE PK_STUDENT_DISBURSEMENT = '$PK_STUDENT_DISBURSEMENT' ");		else if($res_bat->fields['PK_BATCH_STATUS'] == 2)			$db->Execute("UPDATE S_STUDENT_DISBURSEMENT SET PK_DISBURSEMENT_STATUS = '4' WHERE PK_STUDENT_DISBURSEMENT = '$PK_STUDENT_DISBURSEMENT' ");					$res->MoveNext();	}		//If Deposited date and Approved date are set then display "Deposited"	$db->Execute("UPDATE S_STUDENT_DISBURSEMENT SET PK_DISBURSEMENT_STATUS = '1' WHERE APPROVED_DATE != '0000-00-00' AND DEPOSITED_DATE != '0000-00-00' AND PK_STUDENT_MASTER = '$PK_STUDENT_MASTER' $en_cond AND PK_ACCOUNT = '$_SESSION[PK_ACCOUNT]'");}